    /**
     * Sets variables based on the properties of tiles within a given chunk.
     *
     * @param chunk The Chunk object containing tiles to be processed.
     * @author Brett Hammer and Fabian van Os
     */
    private void tileVariableSetter(Chunk chunk) {
        for (int y = 0; y < chunk.getTiles().length; y++) {
            for (int x = 0; x < chunk.getTiles()[y].length; x++) {
                Coordinate coordinate = new Coordinate(x, y);
                Tile tile = chunk.getTile(coordinate);
                TileType type = tile.getType();

                if (type == TileType.GRASS) {
                    emptyTiles.add(tile);
                } else if (type == TileType.SAND) {
                    expectedTiles.add(tile);
                    emptyTiles.add(tile);
                } else if (type == TileType.SAVANNAH) {
                    sut.getGame().getMonsters().add(new Bot(UUID, new Coordinate(x, y), UUID, new Config(), BotType.ZOMBIE));
                }
            }
        }
    }

    /**
     * Test code WPT1
     * Tests if all the monsters in a specific chunk are getting returned.
     *
     * @author Fabian van Os and Lars Meijerink
     */
    @Test
    @DisplayName("Test if the method returns all monsters in the chunk.")
    void testGetAllMonstersInChunk() {
        // Arrange
        Chunk chunk = new Chunk(new Tile[24][80], new Coordinate(0, 0, 0), 0L, UUID);
        Bot zombie = new Bot(UUID, new Coordinate(10, 4), UUID, new Config(), BotType.ZOMBIE);
        zombie.setChunk(chunk);
        sut.getGame().getMonsters().add(zombie);
        List<Bot> expected = sut.getGame().getMonsters();

        // Act
        List<Bot> actual = sut.getAllMonstersInChunk(chunk);

        // Assert
        assertEquals(expected, actual);
    }

